(function () {
    Date.prototype.getTWhours=function () {
        var hours = (new Date()).getHours();
    if (hours > 12) {
        hours -= 12;
    } else if (hours === 0) {
       hours = 12;
    }
    return hours*5;
    }
    sec = function (e, t) {
        this.e = document.getElementsByClassName(e)[0];
        this.t = t;
        this.update = () => {
            let r=((new Date())[this.t]());
            this.e.style.transform = "rotate(" + (r * 6) + "deg)"
        };
    };
    var r = [new sec("sec", "getSeconds"), new sec("hr", "getTWhours"), new sec("min", "getMinutes")];
    setInterval(() => {

        r.forEach(g => g.update());
        document.getElementsByClassName("tcc")[0].innerHTML = (new Date()).toLocaleString();
    }, 1000);
})();
